
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char semaforo = 'r';
		switch (semaforo) {
		case 'r':
			System.out.println("rojo");
		//	break;
		case 'a':
			System.out.println("amarillo");
			break;
		case 'v':
			System.out.println("verde");
			break;
		}
	}

}
